function showGames(gameArray) {
    $('.gamediv').each(function() {
        const pTag = $(this).find('p');
        if (pTag && !gameArray.includes(pTag.text())) {
            $(this).css('display', 'none');
        } else {
            $(this).css('display', 'inline-block');
        }
    });
}

