function searchGame(event) {
    const noResultsElements = document.querySelectorAll('.noresults');
    noResultsElements.forEach((element) => {
        element.parentNode.removeChild(element);
    });
    const searchInput = document.querySelector('.gamesearchbar');
    const searchValue = searchInput.value;
    
    // Check if search bar is empty
    if (searchValue.trim() === '') {
        return;
    }
    
    foundGames = [];

    function addFoundGame(gameName) {
        if (!foundGames.includes(gameName)) {
            foundGames.push(gameName);
        }
    }

    const gameDivs = document.querySelectorAll('.gamediv');
    let foundResults = false;
    gameDivs.forEach((div) => {
        const gameName = div.querySelector('.gameundertext').textContent;
        if (gameName.toLowerCase().includes(searchValue.toLowerCase())) {
            div.style.display = 'inline-block';
            foundResults = true;
            addFoundGame(gameName);
        } else {
            div.style.display = 'none';
        }
    });

    // Search the game descriptions in the list gameDescriptions
    const gameDescriptionsKeys = Object.keys(gameDescriptions);
    gameDescriptionsKeys.forEach((gameKey) => {
        const gameDescription = gameDescriptions[gameKey];
        if (gameDescription.toLowerCase().includes(searchValue.toLowerCase())) {
            foundResults = true;
            addFoundGame(gameKey);
        }
    });

    // Display the found games
    if (foundGames.length > 0) {
        const noResultsElements = document.querySelectorAll('.noresults');
        noResultsElements.forEach((element) => {
            element.parentNode.removeChild(element);
        });
        const noResults = document.querySelector('.noresults');
        if (noResults) {
            noResults.remove();
        }
        foundGames.forEach((gameName) => {
            const gameDivs = document.querySelectorAll('.gamediv');
            gameDivs.forEach((div) => {
                const gameNameElement = div.querySelector('.gameundertext');
                if (gameNameElement.textContent === gameName) {
                    div.style.display = 'inline-block';
                }
            });
        });
    }

    if (!foundResults) {
        const noResults = document.createElement('p');
        noResults.textContent = 'No results found.';
        noResults.style.textAlign = 'center';
        noResults.style.fontFamily = 'Arial, sans-serif';
        noResults.style.color = 'white';
        noResults.style.fontWeight = 'bold';
        noResults.style.fontSize = '24px';
        noResults.className = 'noresults';
        document.body.appendChild(noResults);
    }
}

function addGame() {
    const gameName = prompt('Enter the name of the game:');
    const gameLink = prompt('Enter the link to the game:');
    
    // Add game to the lists
    gameLinks[gameName] = gameLink;
    gameKeys.push(gameName);

    gameDescriptions[gameName] = 'This is a new game added to the list.';

    // Check if the game link has http or https and if it dosen't start with https or http, add it
    if (!gameLink.startsWith('http://') && !gameLink.startsWith('https://')) {
        gameLinks[gameName] = 'https://' + gameLink;
    }

    // Game Image
    const image = 'https://th.bing.com/th/id/OIP.KzTA1dfj3JSb8MTxUyWFzwAAAA?w=240&h=240&rs=1&pid=ImgDetMain&adlt=strict';
    imgList.push(`${gameName}: '${image}'`);

    // Launch game
    loadGame(gameName, image);
}